def solution():
    # Function to calculate the sum of digits of a number
    def sum_of_digits(n):
        return sum(int(digit) for digit in str(n))

    # Initialize the total sum of digits
    total_sum = 0

    # Iterate through all page numbers and sum their digits
    for page_number in range(1, 311):
        total_sum += sum_of_digits(page_number)

    result = total_sum
