def solution():
    """Calculate the number of Blackjack years between 1900 and 2000."""
    blackjack_years_count = 0

    for year in range(1900, 2001):
        if sum(int(digit) for digit in str(year)) == 21:
            blackjack_years_count += 1

    result = blackjack_years_count
    return result
