def solution():
    """Calculate the number of ways Jimmy can climb a flight of stairs with 10 steps, 
    taking 1, 2, or 3 steps at a time, but avoiding the fifth step."""

    # Define a recursive function to calculate the number of ways to reach a given step
    def count_ways(step, avoid):
        # Base cases
        if step < 0:
            return 0
        if step == 0:
            return 1
        if step == avoid:
            return 0

        # Recursive case: sum the ways to reach the current step from the three previous steps
        return count_ways(step - 1, avoid) + count_ways(step - 2, avoid) + count_ways(step - 3, avoid)

    # Calculate the number of ways to reach the 10th step, avoiding the 5th step
    result = count_ways(10, 5)

    return result
