    # Define a function to check if a number is prime
    def is_prime(n):
        if n <= 1:
            return False
        for i in range(2, int(n**0.5) + 1):
            if n % i == 0:
                return False
        return True

    # Initialize a counter for the prime numbers that satisfy the condition
    count = 0

    # Check for prime numbers within 20
    for prime in range(2, 21):
        if is_prime(prime):
            # Check if prime + 6 and prime - 6 are also prime
            if is_prime(prime + 6) or is_prime(prime - 6):
                count += 1

    result = count
    return result

