def solution():
    # The task is to find out how many times each day a 24-hour digital watch shows at least two fours simultaneously.
    # We need to consider all the hours and minutes in a day and count the occurrences where there are at least two fours.

    def count_fours(time_str):
        # Count the number of fours in the time string
        return time_str.count('4')

    # Initialize the count of occurrences
    occurrences = 0

    # Loop through all hours and minutes in a day
    for hour in range(24):
        for minute in range(60):
            # Format the time as a string
            time_str = f"{hour:02d}{minute:02d}"
            # If there are at least two fours, increment the count
            if count_fours(time_str) >= 2:
                occurrences += 1

    result = occurrences
    return result
